import React from 'react';

const Divider = ({ position = 'bottom', color = 'var(--primary-orange)', style = 'wave', className = '' }) => {
  // You can add more styles as needed
  const wave = (
    <svg viewBox="0 0 1440 80" fill="none" xmlns="http://www.w3.org/2000/svg" className={className} style={{ width: '100%', height: '80px', display: 'block' }}>
      <path d="M0 0h1440v40c-120 30-360 60-720 40S120 40 0 80V0z" fill={color} />
    </svg>
  );
  const angle = (
    <svg viewBox="0 0 1440 80" fill="none" xmlns="http://www.w3.org/2000/svg" className={className} style={{ width: '100%', height: '80px', display: 'block' }}>
      <polygon points="0,0 1440,0 1440,80" fill={color} />
    </svg>
  );
  return (
    <div className={`svg-divider-${position} ${className}`} style={{ lineHeight: 0 }}>
      {style === 'angle' ? angle : wave}
    </div>
  );
};

export default Divider;
