import React from 'react';

const FAB = ({ mode = 'contact' }) => {
  const handleClick = () => {
    if (mode === 'contact') {
      // Scroll to contact section if present, else open mailto
      const contact = document.getElementById('contact');
      if (contact) {
        contact.scrollIntoView({ behavior: 'smooth' });
      } else {
        window.location.href = 'mailto:info@pearlsonlanguages.co.ke';
      }
    } else {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };
  return (
    <button
      className="fixed bottom-8 right-8 z-50 bg-[var(--primary-orange)] text-[var(--primary-black)] rounded-full shadow-lg p-4 hover:bg-[#E05A2D] transition-colors duration-200 flex items-center justify-center"
      style={{ width: 56, height: 56 }}
      aria-label={mode === 'contact' ? 'Contact Us' : 'Scroll to Top'}
      onClick={handleClick}
    >
      {mode === 'contact' ? (
        // Envelope icon
        <svg width="28" height="28" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect width="20" height="14" x="2" y="5" rx="2"/><path d="M2 5l10 7 10-7"/></svg>
      ) : (
        // Up arrow icon
        <svg width="28" height="28" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path d="M5 15l7-7 7 7"/><path d="M12 22V8"/></svg>
      )}
    </button>
  );
};

export default FAB;
